import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import {
  Card,
  Button,
  Skeleton,
  Image,
  Typography,
  Tag,
  Divider,
  Space,
  message,
} from "antd";
import {
  ArrowLeftOutlined,
  CalendarOutlined,
  UserOutlined,
  EyeOutlined,
  TagOutlined,
} from "@ant-design/icons";
import { getClubNewsDetail } from "../../services/clubNewsService";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

const { Title, Paragraph, Text } = Typography;

const ClubNewsDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [news, setNews] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNewsDetail = async () => {
      try {
        setLoading(true);
        const response = await getClubNewsDetail(id);
        if (response.success) {
          setNews(response.data);
        } else {
          setError("Không thể tải chi tiết tin tức");
          message.error("Không thể tải chi tiết tin tức");
        }
      } catch (err) {
        console.error("Error fetching news detail:", err);
        setError("Đã xảy ra lỗi khi tải chi tiết tin tức");
        message.error("Đã xảy ra lỗi khi tải chi tiết tin tức");
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchNewsDetail();
    }
  }, [id]);

  const goBack = () => {
    navigate(-1);
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card>
          <Skeleton active avatar paragraph={{ rows: 10 }} />
        </Card>
      </div>
    );
  }

  if (error || !news) {
    return (
      <div className="container mx-auto px-4 py-8 text-center">
        <Title level={3} type="danger">
          {error || "Không tìm thấy tin tức"}
        </Title>
        <Button type="primary" onClick={goBack} icon={<ArrowLeftOutlined />}>
          Quay lại
        </Button>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <Button
        type="text"
        icon={<ArrowLeftOutlined />}
        onClick={goBack}
        className="mb-4"
      >
        Quay lại danh sách tin tức
      </Button>

      <Card className="shadow-md">
        <Title level={2}>{news.title}</Title>

        <Space className="mb-4">
          <Text type="secondary">
            <CalendarOutlined /> Đăng ngày:{" "}
            {new Date(news.publishDate).toLocaleDateString("vi-VN")}
          </Text>
          {news.author && (
            <Text type="secondary">
              <UserOutlined /> {news.author.fullName || news.author.username}
            </Text>
          )}
          {news.tags && news.tags.length > 0 && (
            <>
              <TagOutlined />
              {news.tags.map((tag) => (
                <Tag key={tag} color="blue">
                  {tag}
                </Tag>
              ))}
            </>
          )}
        </Space>

        {news.summary && (
          <Paragraph className="text-lg font-medium italic bg-gray-50 p-4 rounded mb-6">
            {news.summary}
          </Paragraph>
        )}

        {news.image && (
          <div className="mb-6 text-center">
            <Image
              src={`${process.env.REACT_APP_API_URL}/${news.image}`}
              alt={news.title}
              className="rounded max-h-96 object-cover"
              fallback="data:image/png;base64,"
            />
          </div>
        )}

        <Divider />

        <div className="prose max-w-none">
          <ReactMarkdown remarkPlugins={[remarkGfm]}>
            {news.content}
          </ReactMarkdown>
        </div>
      </Card>
    </div>
  );
};

export default ClubNewsDetail;
